<script>
// 1.과일
// 2.치즈
// 3.소고기
// 4.돼지고기
// 5.이탈리아음식
// 6.가금류
// 7.양고기
// 8.해산물
// 9.조개
// 10.생선
// 11.버섯
// 12.샐러드
// 13.견과류
// 14.디저트
export default {
  name: 'FoodSvgGroup',
  data() {
    return {
      svgs: null,
    };
  },
  mounted() {
    this.svgs = document.querySelectorAll('.food-svg-item');
    this.clickedSvg(this.foodId);
  },

  computed: {
    foodId() {
      return this.$store.state.recommendation.foodId;
    },
  },
  methods: {
    clickedSvg(foodId) {
      if (foodId === 0) return;
      this.$emit('clickedSvg', foodId);
      for (let i = 0; i < this.svgs.length; i++) {
        if (i == foodId - 1) {
          this.svgs[i].style.backgroundColor = '#e1aa5786';
        } else {
          this.svgs[i].style.backgroundColor = 'transparent';
        }
      }
    },
  },
};
</script>

<style scoped>
.food-svg-item {
  width: 6rem;
  height: 6rem;
  margin: 1rem 6px 0;
  padding: 1rem;
  fill: var(--basic-color-key);
  border: 1px solid var(--basic-color-key);
  border-radius: 50%;
  cursor: pointer;
  background-color: transparent;
  transition: all 0.5s ease;
}
.food-svg-item:hover {
  background-color: #e1aa5786 !important;
}
</style>
